import React,{Component} from 'react';
import {Auth, CreateUserProfile} from '../../firebase/firebase.utils';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

class Signup extends Component{

    constructor(){
        super();

        this.state = {
            displayName:'',
            email:'',
            password:'',
            confirmPassword:''
        }
    }

    handleSubmit = (evt)=>{

        evt.preventDefault();
    }

    handleChange = (evt)=>{

        evt.preventDefault();

        const {value , name} = evt.target;

        console.log(name , value);

        this.setState({
            [name]: value
        });
    }

    render(){

        const {displayName, email, password, confirmPassword} = this. state;

        return (

            <div className='sign-up'>
                <h1 className='title'>{'I do not have account'}</h1>
                <span>{'Sign up with your email and password'}</span>
                <form className='sign-up-form' onSubmit={this.handleSubmit}>
                    <FormInput 
                        handleChange={this.handleChange}
                        label='name' 
                        type='text' name='dispalyName' value={displayName} required
                    />

                    <FormInput 
                        handleChange={this.handleChange}
                        label='email' 
                        type='email' name='email' value={email} required
                    />

                    <FormInput 
                        handleChange={this.handleChange}
                        label='password' 
                        type='password' name='password' value={password} required
                    />

                    <FormInput 
                        handleChange={this.handleChange}
                        label='confirm password' 
                        type='password' name='confirm-password' value={confirmPassword} required
                    />

                    <CustomButton type='submit'>Sign up</CustomButton>
                </form>
            </div>
        );
    }
}

export default Signup;
