import React,{Component} from 'react';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {Auth} from '../../firebase/firebase.utils';
import {ReactComponent as Logo} from '../../Assets/crown.svg';
import './header.styles.scss';
import CartIcon from '../cart-icon/cart-icon.component';

class Header extends Component{

    constructor(){
        super();

        this.state = {
            offsetY:0
        }
    }

    onScroll = (evt)=>{

        this.setState({offsetY:window.scrollY})

    }

    componentDidMount(){
        window.addEventListener('scroll', this.onScroll);
    }

    componentDidUpdate(){

        let style = {};
        let el = document.querySelector('.header');
        console.log('y', el.offsetHeight);
        if(this.state.offsetY > el.offsetHeight){

            el.style.top = `${this.state.offsetY}px`;
            el.style.backgroundColor = 'lightblue';

        }
        else{

            el.style.top = `${el.offsetHeight}px`;
            el.style.backgroundColor = 'white';
        }

    }

    componentWillUnmount(){
        window.removeEventListener('scroll');
    }

    render(){
        const {currentUser} = this.props;

        return (

            <div className={`header`}>
                <Link className='logo-container' to='/'>
                    <Logo className='logo' />
                </Link>

                <div className='options'>
                    <Link className='option' to='/shop'>{'Shop'}</Link>
                    <Link className='option' to='/contact'>{'Contact'}</Link>
                    {
                        currentUser?
                        <div className='option' onClick={()=>Auth.signOut()}>{'Sign out'}</div>
                        :
                        <Link className='option' to='/signin-signup'>{'Sign in'}</Link>
                    }
                    <CartIcon />
                </div>
            </div>

        );
    }
}

const mapStateToProps = (state)=>({
    currentUser: state.user.currentUser
});

export default connect(mapStateToProps)(Header);
