import firebase from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';

const firebaseConfig = {
    apiKey: "AIzaSyD9V-hXk0igMsffbjAixsev7DvERhxj4nc",
    authDomain: "eclothing-82340.firebaseapp.com",
    databaseURL: "https://eclothing-82340.firebaseio.com",
    projectId: "eclothing-82340",
    storageBucket: "",
    messagingSenderId: "345360672174",
    appId: "1:345360672174:web:5abffc7a33c1d28af4543f"
  };

  firebase.initializeApp(firebaseConfig);

  export const auth = firebase.auth();
  export const firestore = firebase.firestore();

  const provider = new firebase.auth.GoogleAuthProvider();
  provider.setCustomParameters({prompt:'select_account'});
  export const SigninWithGoogle = ()=>auth.signInWithPopup(provider);

  export default firebase;
