import React from 'react';
import {FormGroup, Input, InputLabel} from'./form-input.styles'

const FormInput = ({handleChange, label, highlight, ...otherProps})=>{

    return (

        <FormGroup>
            <Input onChange={handleChange} {...otherProps} />
            {
                label?
                (<InputLabel className='form-input-label' hightlight={highlight} shrink={otherProps.value.length?true:false}>
                {label}
                </InputLabel>)
                :
                null
            }
        </FormGroup>
    );
}

export default FormInput;
