import {takeLatest, all, call, put} from 'redux-saga/effects';
import UserActionTypes from './user.action.type';
import {Auth, googleProvider, CreateUserProfile} from '../../firebase/firebase.utils';
import {GoogleSignInSuccess, GoogleSignInFail,
EmailSignInSuccess, EmailSignInFail} from './user.actions';

export function* signinWithGoogle(){

    try{
        const {user} = yield Auth.signInWithPopup(googleProvider);
        const userRef = yield call(CreateUserProfile, user);
        const userSnapshot = yield userRef.get();

        yield put(GoogleSignInSuccess({
            id:userSnapshot.id,
             ...userSnapshot.data()}));
    }
    catch(error){
        yield put(GoogleSignInFail(error));
    }
}

export function* googleSigninStart(){
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START,
         signinWithGoogle);
}

export function* signinWithEmail({payload:{email, password}}){

    try {
        const {user} = yield Auth.signInWithEmailAndPassword(email, password);
        console.log(user);

    } catch (error) {
        console.log(error);
       yield put(EmailSignInFail(error));
    }
}

export function* emailSigninStart(){
    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START,
         signinWithEmail);
}

export function* userSaga(){
    yield all([call(googleSigninStart), call(emailSigninStart)]);
}
