import styled, {css} from 'styled-components';

const largeMenuItem = css`height: 380px;`;
const smallMenuItem = css`height: 240px;`;

const getMenuItemHeight = (props)=>{
    console.log(props);
    if(props.size === 'large'){
        return largeMenuItem;
    }

    return smallMenuItem;
}

export const MenuItemContainer = styled.div`
    min-width: 30%;
    ${getMenuItemHeight};
    flex: 1 1 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid black;
    margin: 0 7.5px 15px;
    overflow: hidden;

    &:hover{
        cursor: pointer;

        & .background-image{
          transform: scale(1.1);
          transition: transform 6s cubic-bezier(0.25, 0.45, 0.45, 0.95);
        }

        & .content{
          opacity: 0.9;
        }
      }

    }
`;

export const BackgroundImage = styled.img`
    background-image: url(${({imageUrl})=>`url(${imageUrl})`});
    width: 100%;
    height: 100%;
    background-position: center;
    background-size: cover;
`;

export const ContentContainer = styled.div`
    height: 90px;
    padding: 0 25px;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    border: 1px solid black;
    background-color: white;
    opacity: 0.7;
    position: absolute;
`;

export const ContentTitle = styled.h1`
    font-weight: bold;
    margin-bottom: 6px;
    font-size: 22px;
    color: #4a4a4a;
`;

export const ContentSubtitle = styled.span`
    font-weight: lighter;
    font-size: 16px;
`;
